<?phpnamespace App\Http\Middleware;use Closure;use Sentinel;class SentinelPermissons{    /**     * Handle an incoming request.     *     * @param $request     * @param Closure $next     * @return \Illuminate\Http\RedirectResponse     */    public function handle($request, Closure $next)    {        $permission = $request->route()->getName();        if (Sentinel::hasAccess($permission)) {            return $next($request);        }        return redirect()->route('home');    }}